const { RichEmbed } = require("discord.js");



exports.run = (message, args) => {


	var msg = message.content.toUpperCase();
	var sender = message.member.user.tag;
	var senderId = message.member.toString();

	if (args[0] != null)
	{
		if (args[0].toUpperCase() == "CREATE")
			pollCreate(message, args);
		else if (args[0].toUpperCase() == "HELP")
			pollHelp(message);
		else if (args[0].toUpperCase() == "DELETE")
			message.channel.send("Poll Delete");
		else
			pollError(message);
	}
	else
		pollError(message);

}

