function pollHelp(message) {
	const helpEmbed = new RichEmbed()
		.setColor(0x2FEDD6)
		.setTitle("Poll Help")
		.setDescription("Create a Poll using AdriBot!\n"
			+ "***Commands:***\n"
			+ "Create Poll\n"
			+ "-*/ab poll create <#poll_title> <#active_time> <#item1> <#item2> ... <#item5>*\n"
			+ "Delete Poll\n"
			+ "-*/ab poll delete <#poll_title>*\n"
			+ "Poll Help\n"
			+ "-*/ab poll help*"
		);

	message.channel.send(helpEmbed);
}

function pollError(message) {
	message.channel.send("Invalid syntax. Use \"/ab poll help\" for more information.");
}